
public class SimpleStuff {
    // There are markers in the method bodies to make them easier to find in test code.
    // Exercise "addsub"
    public static int add(int a, int b) {
        return a+b; // METHOD BODY add
    }
    
    // Exercise "addsub"
    public static int subtract(int a, int b) {
        return a-b; // METHOD BODY subtract
    }
    
    // Exercise "mul" (hidden test only)
    public static int multiply(int a, int b) {
        return a*b; // METHOD BODY multiply
    }
}
